import React, {Component} from 'react';

import './App.css';
import Button from './components/Button';
import Input from './components/Input';
import ClearButton from './components/ClearButton';

class App extends Component {
  constructor(props){
    super(props);

    this.state = {
      input: "",
      previousNumber: "",
      currentNumber: "",
      operator:""
    };
  }
  addToInput = val =>{
    this.setState({input: this.state.input + val});
  };

  addZeroToInput = val =>{
    // only add zero if there is already a number present
    if (this.state.input !== ""){
      this.setState({input: this.state.input + val});
    }
  };
  addDecimalToInput = val =>{
    // only add addDecimal if there is no current decimal point
    if (this.state.input.indexOf(".") === -1){
      this.setState({input: this.state.input + val});
    }
  };

  clearInput = () =>{
    this.setState({input : ""});
  };

  add = () => {
    this.state.previousNumber = this.state.input;
    this.setState({input:""});
    this.state.operator = "plus";
  }

  render(){
  return (
    <div className="App">
      <div className = "calc-wrapper">
        <div className = "row">
            <Input>{this.state.input}</Input>

        </div>
        <div className = "row">
            <Button handleClick={this.addToInput}>7</Button>
            <Button handleClick={this.addToInput}>8</Button>
            <Button handleClick={this.addToInput}>9</Button>
            <Button handleClick={this.addToInput}>/</Button>
        </div>
        <div className = "row">
            <Button handleClick={this.addToInput}>4</Button>
            <Button handleClick={this.addToInput}>5</Button>
            <Button handleClick={this.addToInput}>6</Button>
            <Button handleClick={this.addToInput}>*</Button>
        </div>
        <div className = "row">
            <Button handleClick={this.addToInput}>1</Button>
            <Button handleClick={this.addToInput}>2</Button>
            <Button handleClick={this.addToInput}>3</Button>
            <Button handleClick={this.add}>+</Button>
        </div>
        <div className = "row">
            <Button handleClick={this.addDecimalToInput}>.</Button>
            <Button handleClick={this.addZeroToInput}>0</Button>
            <Button handleClick={this.addToInput}>=</Button>
            <Button handleClick={this.addToInput}>-</Button>
        </div>
        <div className = "row">
            <ClearButton handleClear={this.clearInput}
            >Clear</ClearButton>

        </div>
      </div>
    </div>
  );
}
}
export default App;
